mapper.select:
	SELECT *
	FROM (SELECT result1.*,ROWNUM rnum 
	  FROM (SELECT num,writer,title,content,viewCount,regdate 
			FROM tp_priboard
		    ORDER BY num DESC
		    ) result1
     )
	WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE tp_priboard
SET viewCount = viewCount+1
WHERE num=#{num}

UPDATE tp_priboard 
SET title=#{title}, content=#{content}
WHERE num=#{num}
